package loopFor;

public class Ex01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		for( i =0;i<5; i++) {
			System.out.println("지금 "+ i +"번 반복중");
			
		}
		System.out.println( "여기서 for문이 끝남" + i);
		
		for(i=1; i<=5;i++)
			System.out.println(i+" ");
	}

}
