package loopFor;

public class Exam07_이중포문 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i , j , count = 0;
		
		for(i=1;i<=10; i++) {
			for(j=1;j<=10;j++)
				count +=1;
			
		}
		System.out.println("count : "+count
				);
		
		
		
		
	
	}
}
